mapper.select:
select (select desp from geocode g where g.code =geocode ) desp, (select decode(substr(code,5,2),'00',0,g.dis_order) from geocode g where g.code =geocode ) dis_order, sum(jybz) as jybz, sum(kybz) as kybz, sum(whbz) as whbz, sum(wsbz) as wsbz, sum(nlmsbz) as nlmsbz, sum(jtbz) as jtbz, sum(qtbz) as qtbz from
(select g.code as geocode,g.desp,s.hylb, nvl(s.QEBKBZRS+s.CZBKBZRS+s.JFZLBZRS,0) as jybz, 0 as kybz, 0 as whbz, 0 as wsbz, 0 as nlmsbz, 0 as jtbz, 0 as qtbz from geocode g left join sy s on g.code=s.geo_code and s.hylb like '01%' where g.code like #{depcode} AND g.code != #{code} union all select g.code as geocode,g.desp,s.hylb, 0 as jybz, nvl(s.QEBKBZRS+s.CZBKBZRS+s.JFZLBZRS,0) as kybz, 0 as whbz, 0 as wsbz, 0 as nlmsbz, 0 as jtbz, 0 as qtbz from geocode g left join sy s on g.code=s.geo_code and s.hylb like '02%' where g.code like #{depcode} AND g.code != #{code} union all select g.code as geocode,g.desp,s.hylb, 0 as jybz, 0 as kybz, nvl(s.QEBKBZRS+s.CZBKBZRS+s.JFZLBZRS,0) as whbz, 0 as wsbz, 0 as nlmsbz, 0 as jtbz, 0 as qtbz from geocode g left join sy s on g.code=s.geo_code and s.hylb like '03%' where g.code like #{depcode} AND g.code != #{code} union all select g.code as geocode,g.desp,s.hylb, 0 as jybz, 0 as kybz, 0 as whbz, nvl(s.QEBKBZRS+s.CZBKBZRS+s.JFZLBZRS,0) as wsbz, 0 as nlmsbz, 0 as jtbz, 0 as qtbz from geocode g left join sy s on g.code=s.geo_code and s.hylb like '04%' where g.code like #{depcode} AND g.code != #{code} union all select g.code as geocode,g.desp,s.hylb, 0 as jybz, 0 as kybz, 0 as whbz, 0 as wsbz, nvl(s.QEBKBZRS+s.CZBKBZRS+s.JFZLBZRS,0) as nlmsbz, 0 as jtbz, 0 as qtbz from geocode g left join sy s on g.code=s.geo_code and s.hylb like '17%' where g.code like #{depcode} AND g.code != #{code} union all select g.code as geocode,g.desp,s.hylb, 0 as jybz, 0 as kybz, 0 as whbz, 0 as wsbz, 0 as nlmsbz, nvl(s.QEBKBZRS+s.CZBKBZRS+s.JFZLBZRS,0) as jtbz, 0 as qtbz from geocode g left join sy s on g.code=s.geo_code and s.hylb like '11%' where g.code like #{depcode} AND g.code != #{code} union all select g.code as geocode,g.desp,s.hylb, 0 as jybz, 0 as kybz, 0 as whbz, 0 as wsbz, 0 as nlmsbz, 0 as jtbz, nvl(s.QEBKBZRS+s.CZBKBZRS+s.JFZLBZRS,0) as qtbz from geocode g left join sy s on g.code=s.geo_code and s.hylb like '25%' where g.code like #{depcode} AND g.code != #{code}
)a group by geocode order by dis_order
