mapper.select:
select jc.jgsy_name as jgname
from jgsy_common jc
where jc.jgsy_states = '1'
  and jc.jgsy_type in ('04', '06', '10')
  and jc.dep_code like #{depCode}
  and jgsy_system_code = #{depName}

select jc.jgsy_name as jgname
from jgsy_common jc
where jc.jgsy_states = '1'
  and jc.jgsy_type = '12'
  and jc.dep_code like #{depCode}
  and jgsy_system_code = #{depName}
